        return new Source("<!DOCTYPE html>

<title>Categorie index</title>

{% block body %}
    <h1>Categorie index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Libelle</th>
                <th>DureeEmpruntMax</th>
                <th>DureeEmpruntMaxNouveaute</th>
                <th>NbEmpruntMax</th>
                <th>NbEmpruntMaxNouveaute</th>
                <th>DureeNouveaute</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for categorie in categories %}
            <tr>
                <td>{{ categorie.id }}</td>
                <td>{{ categorie.libelle }}</td>
                <td>{{ categorie.dureeEmpruntMax }}</td>
                <td>{{ categorie.dureeEmpruntMaxNouveaute }}</td>
                <td>{{ categorie.nbEmpruntMax }}</td>
                <td>{{ categorie.nbEmpruntMaxNouveaute }}</td>
                <td>{{ categorie.dureeNouveaute }}</td>
                <td>
                    <a href=\"{{ path('categorie_show', {'id': categorie.id}) }}\">show</a>
                    <a href=\"{{ path('categorie_edit', {'id': categorie.id}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"8\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('categorie_new') }}\">Create new</a>
{% endblock %}
