    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}
{% block title %}Connexion{% endblock %}
{% block body %}
    <h1>Médiathèque Thales</h1>
    <h2>Connexion</h2>
    {% if app.user %}
        <div class=\"mb-3\">
            Vous êtes déjà connecté en tant que {{ app.user.username }}, <a href=\"{{ path('security_logout') }}\">Se déconnecter ?</a>
        </div>
    {% endif %}
        <form action=\"{{ path('security_login') }}\" method=\"post\">
            {% if error %}
                <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
            {% endif %}
            <div class=\"form-group\">
                <input placeholder=\"Identifiant\" required name=\"_username\" type=\"text\" class=\"form-control\">
            </div>
            <div class=\"form-group\">
                <input placeholder=\"Mot de passe\" required name=\"_password\" type=\"password\" class=\"form-control\">
            </div>
            <div class=\"form-group\">
                <a href=\"\">Mot de passe oublié ?</a>
            </div>

            <div class=\"form-group\">
                <button type=\"submit\" class=\"btn btn-outline-primary\">Connexion</button>
            </div>

            <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">
        </form>
    <p>Pas encore inscrit ? | <a href=\"{{ path('security_registration') }}\">Inscrivez-vous !</a></p>

{% endblock %}", "security/login.html.twig", "/home/lisa/Documents/test/projet/templates/security/login.html.twig");
